let btnRandom = document.querySelector('button');
let result = document.querySelector('h1');



function getRandomWisdom(min, max) {

    let wis1 = max - min + 1;
    let wis2 = Math.random() * wis1;
    let result = Math.floor(wis2) + min;

    return result;

}


 btnRandom.addEventListener('click', () => {
    let index = getRandomWisdom(0,countWisdom.length-1);
    result.innerText = countWisdom[index];
 });